import React, { Component } from 'react';
import './App.css';
import qAPI from './qAPI/qAPI';
import QuestionDisplay from './QuestionDisplay/QuestionDisplay';

class App extends Component {
	constructor() {
		super();
		this.api = new qAPI();
		this.state = {
			question: null, //bad name
			oldQuestion: null,
			guess: '',
			answered: false,
		};
		this.api.getQuestion(0).then(res => this.setState({ question: res }));
	}

	onEnter() {
		this.setState({ answered: true });
	}

	render() {
		return (
			<React.Fragment>
				{/* <h1>STATBOY</h1> */}
				<div>
					<div style={{ position: "fixed", top: "0", left: "0" }}>
						{this.state.question ? (
							<QuestionDisplay q={this.state.question.q} unit={this.state.question.unit} />
						) : (
							''
						)}
					</div>
				</div>
				<input
					onKeyPress={e => (e.keyCode || e.charCode == 13 ? this.onEnter() : '')}
					onChange={e => this.setState({ guess: e.target.value })}
				/>
				<h3 className="text-center">{this.state.answered ? this.state.question.a : 'press enter'} </h3>
				<h3 className="text-left">
					score:
					{this.state.answered
						? Math.log10(Math.abs(Number(this.state.question.a) - Number(this.state.guess)))
						: '---'}{' '}
				</h3>
			</React.Fragment>
		);
	}
}

export default App;
